mapper.select:
select rank_type, name, item_type, uid, id, `time`, count, type from ( select rank_type, name, item_type, uid, id, `time`, row_number() over ( order by id) as count, 301 as type from gacha_cn_301 g where uid = #{uid}) t where t.rank_type = 5
union all ( select rank_type, name, item_type, uid, id, `time`, count, type from ( select rank_type, name, item_type, uid, id, `time`, row_number() over ( order by id) as count, 200 as type from gacha_cn_200 g where uid = #{uid}) t where t.rank_type = 5
) union all ( select rank_type, name, item_type, uid, id, `time`, count, type from ( select rank_type, name, item_type, uid, id, `time`, row_number() over ( order by id) as count, 302 as type from gacha_cn_302 g where uid = #{uid}) t where t.rank_type = 5
) ORDER BY id, time asc
